import { ProductItem } from "./../model/productItem.model";
import { Customer, PageCustomer } from "./../model/customer.model";
import { CustomerService } from "./../services/customer.service";
import { ProductService } from "./../services/product.service";
import { PageProduct, Product } from "./../model/product.model";
import { Bill } from "./../model/bill.model";
import { BillService } from "./../services/bill.service";
import {
  FormGroup,
  FormBuilder,
  Validators,
  ValidationErrors,
} from "@angular/forms";
import { Component, OnInit } from "@angular/core";
import { Router } from "@angular/router";
import { formatDate } from "@angular/common";

@Component({
  selector: "app-new-bill",
  templateUrl: "./new-bill.component.html",
  styleUrls: ["./new-bill.component.scss"],
})
export class NewBillComponent implements OnInit {
  billFormGroup!: FormGroup;
  message: string = "";
  pageProduct!: PageProduct;
  pageCustomer!: PageCustomer;
  selectedProducts: Array<ProductItem> = [];
  billingDate: Date = new Date();
  selectedCustomer: number = -1;
  bill!: Bill;
  selectedProductID: number = 0;

  constructor(
    private fb: FormBuilder,
    private billService: BillService,
    private productService: ProductService,
    private customerService: CustomerService,
    private router: Router
  ) {}
  ngOnInit(): void {
    this.billFormGroup = this.fb.group({
      billingDate: this.fb.control(formatDate(new Date(), "yyyy-MM-dd", "en"), [
        Validators.required,
      ]),
    });
    this.productService.getPageProductsByKeyword("", 0, 100).subscribe({
      next: (data) => {
        this.pageProduct = data;
      },
      error: (err) => alert(err.error),
    });
    this.customerService.getPageCustomersByKeyword("", 0, 100).subscribe({
      next: (data) => {
        this.pageCustomer = data;
        this.selectedCustomer = data._embedded.customers[0]?.id;
      },
      error: (err) => alert(err.error),
    });
  }
  getErrorMessage(name: string, error: ValidationErrors): string {
    if (error["required"]) return `${name}  is required`;
    if (error["minlength"])
      return `The minimum length of ${name} is ${error["minlength"]["requiredLength"]}`;
    if (error["min"])
      return `The min value of ${name} is ${error["min"]["min"]}`;
    return "";
  }
  handleNewBillFormSubmit() {
    const { billingDate } = this.billFormGroup.controls;
    console.log(billingDate.value);

    this.billService
      .addBill({
        billingDate: billingDate.value,
        customerID: this.selectedCustomer,
        productItems: this.selectedProducts,
      })
      .subscribe({
        next: (data) => {
          console.log(data);
          this.router.navigateByUrl("/user/bills");
        },
        error: (err) => console.log(err),
      });
  }
  handleCloseAlert() {
    this.message = "";
  }
  setSelectedCustomer(customerID: number) {
    this.selectedCustomer = customerID;
  }
  addSelectedProduct(productID: number, price: number) {
    this.selectedProducts.push({
      productID: productID,
      price: price,
      quantity: 1,
    });
  }
  removeSelectedProduct(productID: number) {
    this.selectedProducts = this.selectedProducts.filter(
      (p) => p?.productID !== productID
    );
  }
  isSelectedProduct(productID: number) {
    return this.selectedProducts.find((p) => p.productID === productID);
  }
  isSelectedCustomer(customerID: number) {
    return this.selectedCustomer === customerID;
  }
  setQuantityFor(target: any, productID: number) {
    const prodItemIndex = this.selectedProducts.findIndex(
      (p) => p.productID === productID
    );

    if (prodItemIndex >= 0) {
      this.selectedProducts[prodItemIndex].quantity = Number(target.value);
    }
  }
  range(n: number) {
    return [...Array(n + 1).keys()].slice(1);
  }
  setDateValue(target: EventTarget) {
    console.log(target);

    //this.billingDate = date;
  }
}
<div class="container mt-3">
  <div
    *ngIf="message.length > 0"
    class="alert alert-success alert-dismissible fade show"
    role="alert"
  >
    <button type="button" class="close" data-dismiss="alert" aria-label="Close">
      <span aria-hidden="true">&times;</span>
      <span class="sr-only" (click)="handleCloseAlert()">Close</span>
    </button>
    <strong>
      {{ message }}
    </strong>
  </div>
  <div class="container">
    <form class="mb-3" [formGroup]="billFormGroup">
      <label class="row col-sm-12 col-md-6 col-lg-4 col-xl-3 form-label"
        >Billing date</label
      >
      <input
        type="date"
        formControlName="billingDate"
        class="form-control row col-sm-12 col-md-6 col-lg-4 col-xl-3"
      />
      <small
        class="text-danger row col-sm-12 col-md-6 col-lg-4 col-xl-3"
        *ngIf="
          billFormGroup.controls['billingDate'].errors &&
          billFormGroup.controls['billingDate'].touched
        "
      >
        {{
          getErrorMessage(
            "Billing Date",
            billFormGroup.controls["billingDate"].errors
          )
        }}
      </small>
    </form>

    <div class="mb-3">
      <label class="form-label">Customer</label>
      <div class="container">
        <div class="row">
          <div
            class="card col-sm-12 col-md-6 col-lg-4"
            *ngFor="let customer of pageCustomer._embedded.customers"
          >
            <div class="card-body">
              <h5 class="card-title">{{ customer.name }}</h5>
              <p class="card-text">
                {{ customer.email }}
              </p>
              <button
                type="button"
                *ngIf="!isSelectedCustomer(customer.id)"
                class="btn btn-primary"
                (click)="setSelectedCustomer(customer.id)"
              >
                Select
              </button>
              <span
                *ngIf="isSelectedCustomer(customer.id)"
                class="btn bg-dark text-light text-center"
              >
                Selected
              </span>
            </div>
          </div>
        </div>
        <div class="row d-flex mt-4">
          <div class="col-4 d-flex justify-content-start">
            <button class="btn btn-primary">Previous page</button>
          </div>
          <div class="col-1 d-flex justify-content-center">
            {{ pageCustomer.page.number + 1 }} /
            {{ pageCustomer.page.totalPages + 1 }}
          </div>

          <div class="col-3 d-flex justify-content-center">
            {{ pageCustomer.page.size }} per page
          </div>
          <div class="col-4 d-flex justify-content-end">
            <button class="btn btn-primary">Next page</button>
          </div>
        </div>
      </div>
    </div>

    <div class="mb-3">
      <label class="form-label">Products</label>
      <div class="container">
        <div class="row">
          <div
            class="card col-sm-12 col-md-6 col-lg-4"
            *ngFor="let product of pageProduct._embedded.products"
            [ngClass]="
              isSelectedProduct(product.id) ? 'border border-success' : ''
            "
          >
            <div class="card-body">
              <h5 class="card-title">{{ product.name }}</h5>
              <div class="card-text row mb-3">
                <div class="col-6">{{ product.price }} Dhs</div>
                <div class="col-6 d-flex justify-content-end">
                  {{ product.quantity }} Left
                </div>
              </div>
              <div class="row d-flex justify-content-between">
                <select
                  (change)="setQuantityFor($event.target, product.id)"
                  class="form-select mt-2"
                  *ngIf="isSelectedProduct(product.id)"
                >
                  <option
                    [value]="item"
                    *ngFor="let item of range(product.quantity)"
                  >
                    {{ item }}
                  </option>
                </select>
                <button
                  *ngIf="!isSelectedProduct(product.id) && product.quantity > 0"
                  type="button"
                  class="btn btn-success col-xs-12 col-sm-6 col-md-5 col-lg-4 col-xl-3 mt-2"
                  (click)="addSelectedProduct(product.id, product.price)"
                >
                  Add
                </button>

                <button
                  *ngIf="isSelectedProduct(product.id)"
                  type="button"
                  class="btn btn-danger col-xs-12 col-sm-6 col-md-5 col-lg-4 col-xl-3 mt-2"
                  (click)="removeSelectedProduct(product.id)"
                >
                  Remove
                </button>

                <!-- <input
                  class="col-xs-12 col-sm-6 col-md-5 col-lg-4 col-xl-3"
                  [max]="product.quantity"
                  [value]="product.quantity > 0 ? 1 : 0"
                  type="number"
                  (input)="setQuantityFor($event.target, product.id)"
                /> -->
              </div>
            </div>
          </div>
        </div>
        <div class="row d-flex mt-4">
          <div class="col-4 d-flex justify-content-start">
            <button class="btn btn-primary">Previous page</button>
          </div>
          <div class="col-1 d-flex justify-content-center">
            {{ pageProduct.page.number + 1 }} /
            {{ pageProduct.page.totalPages + 1 }}
          </div>

          <div class="col-3 d-flex justify-content-center">
            {{ pageProduct.page.size }} per page
          </div>
          <div class="col-4 d-flex justify-content-end">
            <button class="btn btn-primary">Next page</button>
          </div>
        </div>
      </div>
    </div>

    <div class="mb-3">
      <button
        type="submit"
        class="btn btn-outline-warning"
        (click)="handleNewBillFormSubmit()"
      >
        Add bill
      </button>
    </div>
    <div
      class="modal fade"
      id="productQuantityModal"
      tabindex="-1"
      role="dialog"
      aria-labelledby="productQuantityModalLabel"
      aria-hidden="true"
    >
      <div class="modal-dialog" role="document">
        <div class="modal-content">
          <div class="modal-header">
            <h5 class="modal-title" id="productQuantityModalLabel">
              Product {{ selectedProductID }} quantity
            </h5>
            <button
              type="button"
              class="close"
              data-dismiss="modal"
              aria-label="Close"
            >
              <span aria-hidden="true">&times;</span>
            </button>
          </div>
          <div class="modal-body"></div>
          <div class="modal-footer">
            <button
              type="button"
              class="btn btn-secondary"
              data-dismiss="modal"
            >
              Close
            </button>
            <button type="button" class="btn btn-primary">Save changes</button>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
