import { CustomerService } from "./../services/customer.service";
import { Component, OnInit } from "@angular/core";
import {
  Form,
  FormBuilder,
  Validators,
  FormGroup,
  ValidationErrors,
} from "@angular/forms";

@Component({
  selector: "app-new-customer",
  templateUrl: "./new-customer.component.html",
  styleUrls: ["./new-customer.component.scss"],
})
export class NewCustomerComponent implements OnInit {
  handleAddCustomerFormSubmit() {
    const { name, email } = this.customerFormGroup.value;
    this.customerService.addCustomer(name, email).subscribe({
      next: (customer) => {
        this.customerFormGroup.reset();
        this.message = "Customer added successfully";
      },
      error: (err) => console.log(err),
    });
  }
  handleCloseAlert() {
    this.message = "";
  }
  customerFormGroup!: FormGroup;
  message: string = "";
  constructor(
    private fb: FormBuilder,
    private customerService: CustomerService
  ) {}
  ngOnInit(): void {
    this.customerFormGroup = this.fb.group({
      name: this.fb.control("", [Validators.required, Validators.minLength(2)]),
      email: this.fb.control("", [
        Validators.required,
        Validators.minLength(4),
      ]),
    });
  }
  getErrorMessage(name: string, error: ValidationErrors): string {
    if (error["required"]) return `${name}  is required`;
    if (error["minlength"])
      return `The minimum length of ${name} is ${error["minlength"]["requiredLength"]}`;
    if (error["min"])
      return `The min value of ${name} is ${error["min"]["min"]}`;
    return "";
  }
}
<div class="container mt-3 col-md-6 offset-md-3">
  <div
    *ngIf="message.length > 0"
    class="alert alert-success alert-dismissible fade show"
    role="alert"
  >
    <button type="button" class="close" data-dismiss="alert" aria-label="Close">
      <span aria-hidden="true">&times;</span>
      <span class="sr-only" (click)="handleCloseAlert()">Close</span>
    </button>
    <strong>
      {{ message }}
    </strong>
  </div>
  <form
    *ngIf="customerFormGroup"
    [formGroup]="customerFormGroup"
    (ngSubmit)="handleAddCustomerFormSubmit()"
  >
    <div class="mb-3">
      <label class="form-label">Customer name</label>
      <input type="text" formControlName="name" class="form-control" />
      <small
        class="text-danger"
        *ngIf="
          customerFormGroup.controls['name'].errors &&
          customerFormGroup.controls['name'].touched
        "
      >
        {{ getErrorMessage("Name", customerFormGroup.controls["name"].errors) }}
      </small>
    </div>
    <div class="mb-3">
      <label class="form-label">Customer email</label>
      <input type="email" formControlName="email" class="form-control" />
      <small
        class="text-danger"
        *ngIf="
          customerFormGroup.controls['email'].errors &&
          customerFormGroup.controls['email'].touched
        "
      >
        {{
          getErrorMessage("Email", customerFormGroup.controls["email"].errors)
        }}
      </small>
    </div>

    <div class="mb-3">
      <button
        type="submit"
        class="btn btn-outline-success"
        [disabled]="!customerFormGroup.valid"
      >
        Add customer
      </button>
    </div>
  </form>
</div>
