import { Product } from "./../model/product.model";
import { ProductService } from "./../services/product.service";
import {
  FormGroup,
  FormBuilder,
  Validators,
  ValidationErrors,
} from "@angular/forms";
import { Component, OnInit } from "@angular/core";
import { ActivatedRoute, Route, Router } from "@angular/router";

@Component({
  selector: "app-edit-product",
  templateUrl: "./edit-product.component.html",
  styleUrls: ["./edit-product.component.scss"],
})
export class EditProductComponent implements OnInit {
  productFormGroup!: FormGroup;
  message: string = "";
  productID!: number;
  constructor(
    private fb: FormBuilder,
    private productService: ProductService,
    private router: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.productID = Number(this.router.snapshot.params["id"]);
    this.productService.getProductById(this.productID).subscribe({
      next: (product) => {
        console.log(product);
        this.productFormGroup = this.fb.group({
          id: this.fb.control(product.id),
          name: this.fb.control(product.name, [Validators.required]),
          price: this.fb.control(product.price, [
            Validators.required,
            Validators.min(1),
          ]),
          quantity: this.fb.control(product.quantity, [
            Validators.required,
            Validators.min(0),
          ]),
        });
      },
      error: (err) => alert(err.error),
    });
  }

  getErrorMessage(name: string, error: ValidationErrors): string {
    if (error["required"]) return `${name}  is required`;
    if (error["minlength"])
      return `The minimum length of ${name} is ${error["minlength"]["requiredLength"]}`;
    if (error["min"])
      return `The min value of ${name} is ${error["min"]["min"]}`;
    return "";
  }

  handleEditProductFormSubmit() {
    const { id, name, price, quantity } = this.productFormGroup.value;
    this.productService.editProduct(id, name, price, quantity).subscribe({
      next: (product) => {
        this.productFormGroup.reset();
        this.message = "Product edited successfully";
      },
      error: (err) => console.log(err),
    });
  }

  handleCloseAlert() {
    this.message = "";
  }
}
<div class="container mt-3 col-md-6 offset-md-3">
  <div
    *ngIf="message.length > 0"
    class="alert alert-success alert-dismissible fade show"
    role="alert"
  >
    <button type="button" class="close" data-dismiss="alert" aria-label="Close">
      <span aria-hidden="true">&times;</span>
      <span class="sr-only" (click)="handleCloseAlert()">Close</span>
    </button>
    <strong>
      {{ message }}
    </strong>
  </div>
  <form
    *ngIf="productFormGroup"
    [formGroup]="productFormGroup"
    (ngSubmit)="handleEditProductFormSubmit()"
  >
    <div class="mb-3">
      <label class="form-label">Product ID</label>
      <input type="number" formControlName="id" class="form-control" readonly />
    </div>

    <div class="mb-3">
      <label class="form-label">Product name</label>
      <input type="text" formControlName="name" class="form-control" />
      <small
        class="text-danger"
        *ngIf="
          productFormGroup.controls['name'].errors &&
          productFormGroup.controls['name'].touched
        "
      >
        {{ getErrorMessage("Name", productFormGroup.controls["name"].errors) }}
      </small>
    </div>

    <div class="mb-3">
      <label class="form-label">Product price</label>
      <input type="number" formControlName="price" class="form-control" />
      <small
        class="text-danger"
        *ngIf="
          productFormGroup.controls['price'].errors &&
          productFormGroup.controls['price'].touched
        "
      >
        {{
          getErrorMessage("Price", productFormGroup.controls["price"].errors)
        }}
      </small>
    </div>

    <div class="mb-3">
      <label class="form-label">Product quantity in stock</label>
      <input type="number" formControlName="quantity" class="form-control" />
      <small
        class="text-danger"
        *ngIf="
          productFormGroup.controls['price'].errors &&
          productFormGroup.controls['price'].touched
        "
      >
        {{
          getErrorMessage("Price", productFormGroup.controls["price"].errors)
        }}
      </small>
    </div>

    <div class="mb-3">
      <button
        type="submit"
        class="btn btn-outline-warning"
        [disabled]="!productFormGroup.valid"
      >
        Edit product
      </button>
    </div>
  </form>
</div>
